import React, { useState } from 'react';
import './styles/Login.css';
import { Box, Button, TextField } from '@material-ui/core';
import EmailIcon from '@material-ui/icons/Email';
import LockIcon from '@material-ui/icons/Lock';
import Grid from '@material-ui/core/Grid';
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import CheckBoxOutlineBlankIcon from '@material-ui/icons/CheckBoxOutlineBlank';
import CheckBoxIcon from '@material-ui/icons/CheckBox';
import Link from '@material-ui/core/Link';
import Logo from './images/logo.png';
import clsx from 'clsx';
import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';

const checkPasswordStrength = (password) => {
  // Regular expressions to check for different character types
  const uppercaseRegex = /[A-Z]/;
  const lowercaseRegex = /[a-z]/;
  const numberRegex = /[0-9]/;
  const specialCharRegex = /[!@#$%^&*()_+\-=[\]{};':"\\|,.<>/?]/;

  // Check if the password contains each character type
  const hasUppercase = uppercaseRegex.test(password);
  const hasLowercase = lowercaseRegex.test(password);
  const hasNumber = numberRegex.test(password);
  const hasSpecialChar = specialCharRegex.test(password);

  // Calculate the strength level based on the presence of different character types
  let strengthLevel = 'Weak';

  if (hasUppercase && hasLowercase && hasNumber && hasSpecialChar) {
    strengthLevel = 'Strong';
  } else if ((hasUppercase || hasLowercase) && (hasNumber || hasSpecialChar)) {
    strengthLevel = 'Medium';
  }

  return strengthLevel;
};

const Login = () => {
  const [values, setValues] = useState({
    email: '',
    password: '',
    showPassword: false,
    passwordError: '',
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ ...values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Dummy user credentials
    const dummyUser = {
      email: 'jamescalvo142@gmail.com',
      password: 'Jessiejames123',
    };

    // Case-insensitive comparison for email
    const enteredEmail = values.email.toLowerCase().trim();
    const dummyEmail = dummyUser.email.toLowerCase().trim();

    // Check if entered email and password match the dummy user's credentials
    if (enteredEmail === dummyEmail && values.password.trim() === dummyUser.password) {
      // If matched, login successful
      console.log('Login successful');
      // Add redirection or any other logic for successful login
    } else {
      // If not matched, display error message
      console.log('Login failed'); // Check if this message is logged
      setValues({ ...values, passwordError: 'Invalid email or password' });
    }
  };

  const passwordStrength = checkPasswordStrength(values.password);

  return (
    <>
      <Box container boxShadow={3} margin="10% auto" alignItems="center" width={500} height={360}>
        <img src={Logo} alt="logo" className="logo" /><br />
        <form onSubmit={handleSubmit} autoComplete="off">

          <Grid container spacing={1} alignItems="flex-end">
            <Grid item>
              <EmailIcon className="mrl" />
            </Grid>
            <Grid item>
              <TextField
                name='email'
                required
                id="input-with-icon-grid"
                label="Email"
                color="secondary"
                className="email"
                value={values.email}
                onChange={handleChange('email')}
              />
            </Grid>
          </Grid>

          <Grid container spacing={1} alignItems="flex-end">
            <Grid item>
              <LockIcon className="mrl" />
            </Grid>
            <Grid item>
              <FormControl className={clsx()}>

                <InputLabel htmlFor="standard-adornment-password" style={{ marginLeft: '15%' }}>Password*</InputLabel>
                <Input
                  label="Password"
                  color="secondary"
                  className="password"
                  id="standard-adornment-password"
                  type={values.showPassword ? 'text' : 'password'}
                  value={values.password}
                  onChange={handleChange('password')}
                  endAdornment={
                    <InputAdornment position="end">
                      <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowPassword}
                        onMouseDown={handleMouseDownPassword}
                      >
                        {values.showPassword ? <Visibility /> : <VisibilityOff />}
                      </IconButton>
                    </InputAdornment>
                  }
                />
              </FormControl>
            </Grid>
          </Grid>

          <Grid container spacing={3}>
            <Grid item xs={6} className="gd">
              <FormControlLabel control={<Checkbox icon={<CheckBoxOutlineBlankIcon fontSize="small" />} checkedIcon={<CheckBoxIcon fontSize="small" />} name="checkedI" />} label="Secure Login" />
            </Grid>
            <Grid item xs={6}>
              <Link href="#" color="secondary" className="gd1">
                {'Forget Password'}
              </Link>
            </Grid>
          </Grid>
          <Button variant="contained" color="secondary" className="btn" size="large" type="submit">Login</Button>
          {values.passwordError && <p style={{ color: 'red' }}>{values.passwordError}</p>}
          <p>Password Strength: {passwordStrength}</p>
        </form>
        <p style={{ color: '#6b6a6a', textAlign: 'center' }}>All Right Reserved</p>
      </Box>
      <br />
    </>
  );
}

export default Login;
